package com.melody.castle.utils;import java.util.regex.Matcher;import java.util.regex.Pattern;public class CheckUtils {	public static boolean checkEmail(String email){		if( email == null || email.length() < 1 )			return false;				 String regex = RegularMatcher.EMAIL_EXPRESS;		 Pattern p = Pattern.compile(regex);		 Matcher m = p.matcher(email);		 boolean isNormal = m.matches();		 return isNormal;	}		public static boolean checkURL(String url){		if( url == null || url.length() < 1 )			return false;				 String regex = RegularMatcher.URL_EXPRESS;		 Pattern p = Pattern.compile(regex);		 Matcher m = p.matcher(url);		 boolean isNormal = m.matches();		 return isNormal;	}}